                background: #BB025E;
                height: 47px;
                display: flex;
                align-items: center;
                justify-content: center;
            }

            .usp-bar .usps {
                /* display: none; */
                list-style-type: none;

                margin: unset;
                display: flex;
                flex-direction: row;
                justify-content: space-around;
                align-items: center;
            }

            .usp-bar .usps li span {
                color: #fff;
                font-weight: 300;
                margin-left: 5px;
                font-size: 16px;
                font-family: 'Roboto', sans-serif;
            }
        }
