        .usp-bar {
            display: none;
        }

        .mobile-usp-bar {
            height: 47px;
            background: #BB025E;
            display: flex;
            align-items: center;
        }

        .mobile-usp-bar>div {
            animation-duration: 20s;
            /* width: 100%; */
            position: absolute;
            left: 50%;
            transform: translateX(-50%);
            opacity: 0;
        }

        .mobile-usp-bar .usp span {
            color: #fff;
            font-weight: 300;
            margin-left: 5px;
            font-size: 16px;
            font-family: 'Roboto', sans-serif;
        }

        .mobile-usp-bar>div:nth-of-type(1) {
            animation-name: usp1;
            animation-iteration-count: infinite;
        }

        .mobile-usp-bar>div:nth-of-type(2) {
            animation-name: usp2;
            animation-iteration-count: infinite;
        }

        .mobile-usp-bar>div:nth-of-type(3) {
            animation-name: usp3;
            animation-iteration-count: infinite;
        }

        .mobile-usp-bar>div:nth-of-type(4) {
            animation-name: usp4;
            animation-iteration-count: infinite;
        }
    }
</style>

